package whileloop;

public class Mathclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.PI);
		//also
		double pi=Math.PI;
		System.out.println(pi);
		//
		System.out.println(Math.addExact(1009, 9860));
		System.out.println(Math.addExact(287675644, 0757465364));
		System.out.println(Math.subtractExact(100, 45));
		System.out.println(Math.multiplyExact(234, 676));
		System.out.println(Math.sqrt(55));
		System.out.println(Math.max(39993.8, 6776.76));
		for(int i=0;i<100;i++) {
		System.out.println(Math.random());}//everytime it change from 0 to 1
	}

}
